mapper.update:


   		UPDATE T_GJ_REAL_DATA
   		SET   VC_GJ_VAL = #{value}, VC_REFRESH_TIME = #{collectTime}
   		WHERE VC_GJ_KEY = #{key} AND VC_GJ_CD = #{cd}

   		IF @@ROWCOUNT = 0

   		INSERT INTO T_GJ_REAL_DATA (
			VC_GJ_KEY, VC_GJ_VAL, VC_GJ_CD, VC_REFRESH_TIME
		)
		VALUES(
			#{key}, #{value}, #{cd}, #{collectTime}
		)



   		UPDATE T_LK_REAL_DATA
   		SET VC_LK_TYPE = #{type}
		      ,VC_LK_AGENT = #{agent}
		      ,VC_LK_V = #{v}
		      ,VC_LK_T = #{t}
		      ,VC_LK_E = #{e}
		      ,VC_LK_C = #{c}
		      ,VC_LK_M = #{m}
		      ,VC_LK_EV = #{ev}
		      ,VC_LK_CLAS = #{clas}
		      ,VC_LK_Z = #{z}
		      ,VC_LK_ALARM = #{alarm}
		      ,VC_LK_U = #{u}
		      ,VC_REFRESH_TIME = #{collectTime}
   		WHERE VC_LK_D = #{d} AND VC_LK_ID = #{id} AND VC_LK_CD = #{cd}

   		IF @@ROWCOUNT = 0

   		INSERT INTO T_LK_REAL_DATA (
			VC_LK_TYPE
	      ,VC_LK_AGENT
	      ,VC_LK_D
	      ,VC_LK_ID
	      ,VC_LK_V
	      ,VC_LK_T
	      ,VC_LK_E
	      ,VC_LK_C
	      ,VC_LK_M
	      ,VC_LK_EV
	      ,VC_LK_CLAS
	      ,VC_LK_Z
	      ,VC_LK_ALARM
	      ,VC_LK_U
	      ,VC_LK_CD
	      ,VC_REFRESH_TIME
		)
		VALUES(
			 #{type}
		      ,#{agent}
		      ,#{d}
		      ,#{id}
		      ,#{v}
		      ,#{t}
		      ,#{e}
		      ,#{c}
		      ,#{m}
		      ,#{ev}
		      ,#{clas}
		      ,#{z}
		      ,#{alarm}
		      ,#{u}
		      ,#{cd}
		      ,#{collectTime}
		)
